import React from 'react';
import styled from 'styled-components';

// top flex container component for a page
export const FBase = styled.div`
  display: flex;
  flex-direction: row;
  min-height: 100vh;
  align-items: ${({ alignItems }) => alignItems ? alignItems : 'flex-start'};
  justify-content: ${({ justifyContent }) => justifyContent ? justifyContent : 'flex-start'};
  background-color: ${ ({ theme }) => theme.colors.background};
`;

// props can be edited
export const FlexBase = ({ children, alignItems, justifyContent, ...props }) => {
  return (
    <FBase {...props} alignItems={alignItems} justifyContent={justifyContent}>{children}</FBase>
  )
};
