import React from 'react';
import styled, { css } from 'styled-components';
import { Avatar } from '../Avatar';

const UserCardContainer = styled.div`
  display:flex;
  flex-direction: row;
  width: 300px;
  /* border: 2px solid red; */

`;

const AvatarContainer = styled.div`
  flex:1;
  margin: 0 25px 0;
  align-self: center;
  justify-self: center;
`;

const Labels = styled.div`
  /* border: 2px solid red; */
  flex:4;
  flex-direction: column;
`;

/// make it reusable fix here the div/p issue
const Label = styled.div`
  /* border: 2px solid red; */
  word-break: break-all;
  font-family: ${({ theme }) => theme.fonts.body};
  color: ${({ theme }) => theme.colors.primaryBlue};
  font-style: normal;

  ${({ bold }) => bold ? css`
    font-weight: bold;
    font-size: 18px;
    line-height: 25px;`
    :
    css`font-weight: normal;
    font-size: 16px;
    line-height: 22px;`
  };
`;

export const UserCard = ({ fullName, companyName }) => {
  return (
    <UserCardContainer>
      <AvatarContainer>
        <Avatar />
      </AvatarContainer>
      <Labels>
        <Label bold >{fullName || 'Name Lastname'}</Label>
        <Label >{companyName || 'Company name'}</Label>
      </Labels>
    </UserCardContainer>
  )
};
