import React, { useState, useContext } from "react";
import { ThemeProvider, ThemeContext } from "styled-components";
import { colors as c } from './colors';
import { fonts as f } from './fonts';

const Mode = (
  window.matchMedia &&
  window.matchMedia('(prefers-color-scheme: dark)').matches
) ? 'light' : 'light';

export const useTheme = () => useContext(ThemeContext);

const Theme = ({ children }) => {
  const [theme, setTheme] = useState(Mode)
  return (
    <ThemeProvider
      theme={{
        colors: (themes[theme]).colors,
        fonts: (themes[theme]).fonts,
        setTheme, dark: (themes[theme]).dark,
        theme: theme
      }}
    >
      {children}
    </ThemeProvider>
  )
};

export default Theme;

export const themes = {
  light: {
    dark: false,
    colors: {
      background: c.background,
      primaryBlue: c.primaryBlue,
      lightBlue: c.lightBlue,
      black: c.black,
      white: c.white,
      gray: c.gray,
      lightgray: c.lightgray,
      warning: c.warning,
      green: c.green,
      green1: c.green1,
      transparentButtonsHover: c.transparentButtonsHover,
      buttonHover: c.buttonHover,
      buttonHoverBlue: c.buttonHoverBlue,

    },
    fonts: {
      title: f.title,
      body: f.body,
    },
  }
};
